import React, { createContext, useContext, useState } from 'react';

const ListingContext = createContext();

export function ListingProvider({ children }) {
  const [listings, setListings] = useState([]);
  const [messages, setMessages] = useState([]);

  const addListing = (listing) => {
    const newListing = { id: Date.now().toString(), ...listing };
    setListings((prev) => [...prev, newListing]);
  };

  const addMessage = (msg) => {
    const newMsg = { id: Date.now().toString(), ...msg };
    setMessages((prev) => [...prev, newMsg]);
  };

  const deleteListing = (id) => {
  setListings(prev => prev.filter(listing => listing.id !== id));
  setMessages(prev => prev.filter(msg => msg.listingId !== id));
};

const deleteMessage = (id) => {
  setMessages(prev => prev.filter(msg => msg.id !== id));
};

  return (
    <ListingContext.Provider value={{
  listings,
  messages,
  addListing,
  addMessage,
  deleteListing,
  deleteMessage
}}>

      {children}
    </ListingContext.Provider>
  );
}

export const useListing = () => {
  const context = useContext(ListingContext);
  if (!context) throw new Error("useListing must be used within ListingProvider");
  return context;
};
