import { useListing } from '../context/ListingContext';

export default function SellerInbox() {
  const { listings, messages } = useListing();

  // Group messages under their respective listing
  const listingsWithMessages = listings.map((listing) => {
    const relatedMessages = messages.filter(msg => msg.listingId === listing.id);
    return { ...listing, messages: relatedMessages };
  });

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold text-green-800 mb-6">ðŸ“¬ Seller Inbox</h2>
      {listingsWithMessages.length === 0 ? (
        <p>You havenâ€™t posted any products yet.</p>
      ) : (
        listingsWithMessages.map(listing => (
          <div key={listing.id} className="mb-6 border rounded-lg p-4 shadow">
            <h3 className="text-2xl font-semibold text-green-700">{listing.productName}</h3>
            <p className="text-sm text-gray-600 mb-2">Category: {listing.category} â€¢ Location: {listing.location}</p>

            {listing.messages.length === 0 ? (
              <p className="text-gray-500 italic">No messages yet for this listing.</p>
            ) : (
              <ul className="mt-2 space-y-2">
                {listing.messages.map((msg) => (
                  <li key={msg.id} className="border p-3 rounded bg-green-50">
                    <p><strong>Buyer Name:</strong> {msg.buyerName}</p>
                    <p><strong>Phone:</strong> {msg.buyerPhone}</p>
                    <p><strong>Quantity Wanted:</strong> {msg.quantityWanted}</p>
                  </li>
                ))}
              </ul>
            )}
          </div>
        ))
      )}
    </div>
  );
}
