import { useListing } from '../context/ListingContext';
import { useState } from 'react';

export default function BuyerDashboard() {
  const { listings, addMessage } = useListing();
  const [selectedListingId, setSelectedListingId] = useState(null);
  const [message, setMessage] = useState({
    buyerName: '',
    buyerPhone: '',
    quantityWanted: '',
  });

  const handleSend = (e) => {
    e.preventDefault();
    addMessage({ ...message, listingId: selectedListingId });
    alert("Message sent to seller!");
    setMessage({ buyerName: '', buyerPhone: '', quantityWanted: '' });
    setSelectedListingId(null);
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold text-green-800 mb-4">Available Produce</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {listings.length === 0 ? (
          <p>No produce listed yet.</p>
        ) : (
          listings.map((listing) => (
            <div key={listing.id} className="border p-4 rounded shadow">
              <img src={listing.imageUrl} alt={listing.productName} className="w-full h-48 object-cover rounded mb-2" />
              <h3 className="text-xl font-bold text-green-900">{listing.productName}</h3>
              <p>Category: {listing.category}</p>
              <p>Quantity: {listing.quantity}</p>
              <p>Price: KES {listing.price}</p>
              <p>Location: {listing.location}</p>
              <button
                onClick={() => setSelectedListingId(listing.id)}
                className="mt-2 px-4 py-2 bg-green-700 text-white rounded hover:bg-green-600"
              >
                I'm Interested
              </button>

              {selectedListingId === listing.id && (
                <form onSubmit={handleSend} className="mt-4 space-y-2">
                  <input
                    required
                    placeholder="Your Name"
                    value={message.buyerName}
                    onChange={(e) => setMessage({ ...message, buyerName: e.target.value })}
                    className="w-full border p-2 rounded"
                  />
                  <input
                    required
                    placeholder="Phone Number"
                    value={message.buyerPhone}
                    onChange={(e) => setMessage({ ...message, buyerPhone: e.target.value })}
                    className="w-full border p-2 rounded"
                  />
                  <input
                    required
                    placeholder="Quantity You Want"
                    value={message.quantityWanted}
                    onChange={(e) => setMessage({ ...message, quantityWanted: e.target.value })}
                    className="w-full border p-2 rounded"
                  />
                  <button
                    type="submit"
                    className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                  >
                    Send Message
                  </button>
                </form>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
}
