import { useState } from 'react';
import { useListing } from '../context/ListingContext';

export default function SellerDashboard() {
  const { addListing } = useListing();
  const [form, setForm] = useState({
    productName: '', category: '', quantity: '',
    price: '', location: '', imageUrl: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addListing(form);
    alert('Listing submitted for approval.');
    setForm({ productName: '', category: '', quantity: '', price: '', location: '', imageUrl: '' });
  };

  return (
    <div className="flex-1 p-6">
      <h2 className="text-3xl font-bold text-green-900 mb-4">Sell Your Produce</h2>
      <form onSubmit={handleSubmit} className="space-y-4 max-w-md">
        {Object.keys(form).map((field) => (
          <input
            key={field}
            required
            name={field}
            placeholder={field.replace(/([A-Z])/g, ' $1')}
            value={form[field]}
            onChange={handleChange}
            className="w-full border rounded p-2"
          />
        ))}
        <button type="submit" className="bg-green-700 text-white px-4 py-2 rounded">Submit Listing</button>
      </form>
    </div>
  );
}
