import { useListing } from '../context/ListingContext';

export default function AdminPanel() {
  const { listings, messages, deleteListing, deleteMessage } = useListing();

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold text-green-800 mb-6">ðŸ›  Admin Panel</h2>

      {/* All Listings */}
      <div className="mb-8">
        <h3 className="text-2xl font-semibold mb-4 text-green-700">All Listings</h3>
        {listings.length === 0 ? (
          <p className="text-gray-600">No listings available.</p>
        ) : (
          listings.map((listing) => (
            <div key={listing.id} className="border rounded p-4 mb-4 shadow bg-white">
              <h4 className="text-xl font-bold">{listing.productName}</h4>
              <p>Category: {listing.category}</p>
              <p>Quantity: {listing.quantity}</p>
              <p>Price: {listing.price}</p>
              <p>Location: {listing.location}</p>
              <img src={listing.imageUrl} alt={listing.productName} className="w-full max-w-sm rounded mt-2" />

              <button
                onClick={() => deleteListing(listing.id)}
                className="mt-3 inline-block bg-green-800 text-white px-4 py-2 rounded hover:bg-green-800"
              >
                Delete Listing
              </button>
            </div>
          ))
        )}
      </div>

      {/* All Buyer Messages */}
      <div>
        <h3 className="text-2xl font-semibold mb-4 text-green-700">All Buyer Messages</h3>
        {messages.length === 0 ? (
          <p className="text-gray-600">No messages from buyers yet.</p>
        ) : (
          messages.map((msg) => {
            const relatedListing = listings.find(l => l.id === msg.listingId);
            return (
              <div key={msg.id} className="border rounded p-4 mb-3 bg-green-50">
                <p><strong>Buyer Name:</strong> {msg.buyerName}</p>
                <p><strong>Phone:</strong> {msg.buyerPhone}</p>
                <p><strong>Wants:</strong> {msg.quantityWanted}</p>
                {relatedListing && (
                  <p className="text-sm text-gray-600 italic">
                    For listing: <strong>{relatedListing.productName}</strong> ({relatedListing.category})
                  </p>
                )}
                <button
                  onClick={() => deleteMessage(msg.id)}
                  className="mt-2 bg-green-800 text-white px-3 py-1 rounded hover:bg-red-700"
                >
                  Delete Message
                </button>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
