import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Navbar() {
  const { user, logout } = useAuth();

  return (
    <nav className="bg-green-800 text-white p-4">
      <div className="container mx-auto flex items-center justify-between">
        <Link to="/" className="text-2xl font-bold">AGRISOKO</Link>

        <div className="space-x-4 flex items-center">
          <Link to="/" className="hover:underline">Home</Link>
          <Link to="/seller" className="hover:underline">Sell</Link>
          <Link to="/buyer" className="hover:underline">Buy</Link>
          <Link to="/inbox" className="hover:underline">Inbox</Link>
          <Link to="/admin" className="hover:underline">Admin</Link>

          {user ? (
            <>
              <span className="ml-4 font-semibold text-sm">
                Hi, {user.username} ({user.role})
              </span>
              <button
                onClick={logout}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
              >
                Logout
              </button>
            </>
          ) : (
            <Link to="/login" className="bg-white text-green-800 px-3 py-1 rounded hover:bg-green-100">
              Login / Register
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}
